<template>
  <div></div>
</template>

<script>
export default {
  props: {
    latitude: Number,
    longitude: Number,
    address: String,
    name: String,
    label: String,
    // 设置地图缩放级别
    zoom: {
      type: Number,
      default: 17.2
    },
    // 设置俯仰角
    pitch: {
      type: Number,
      default: 43.5
    },
    // 设置地图旋转角度
    rotation: {
      type: Number,
      default: 45
    },
    labelStyle: {
      type: Object,
      required: false,
      default() {
        return {}
      },
    },
    markerStyle: {
      type: Object,
      default() {
        return {}
      },
      required: false,
    }
  },
  mounted() {
    const { latitude, longitude, label, zoom, pitch, rotation, labelStyle, markerStyle } = this.$props

    const TMap = window.TMap
    if (!TMap) return

    const center = new TMap.LatLng(latitude, longitude)
    const map = new TMap.Map(this.$el, {
      center: center,
      zoom,
      pitch,
      rotation,
    });

    //创建并初始化MultiMarker
    const styleId = 'myStyle'
    new TMap.MultiMarker({
      map,
      styles: {
        [styleId]: new TMap.MarkerStyle({
          width: 40,
          height: 40,
          src: "data:image/png;base64,", //图片路径
          //焦点在图片中的像素位置，一般大头针类似形式的图片以针尖位置做为焦点，圆形点以圆心位置为焦点
          anchor: { x: 20, y: 40 },
          ...markerStyle,
        }),
      },
      geometries: [
        {
          id: "marker1",
          styleId,
          position: center,
        },
      ],
    })

    new TMap.MultiLabel({
      id: 'label-layer',
      map,
      styles: {
        [styleId]: new TMap.LabelStyle({
          color: "#3777FF",
          size: 20,
          offset: { x: 0, y: 20 }, // 文字偏移属性单位为像素
          angle: 0, // 文字旋转属性
          alignment: "center", // 文字水平对齐属性
          verticalAlignment: "middle", //文字垂直对齐属性
          ...labelStyle,
        }),
      },
      geometries: [
        {
          id: "label_1",
          styleId,
          position: center,
          content: label,
        },
      ],
    })
  },
  methods: {
    openLocation(scale, infoUrl = '') {
      const { latitude, longitude, zoom, name, address } = this.$props
      const wx = window.wx
      ws && wx.openLocation({
        latitude, // 纬度，浮点数，范围为90 ~ -90
        longitude, // 经度，浮点数，范围为180 ~ -180。
        name, // 位置名
        address, // 地址详情说明
        scale: scale || zoom, // 地图缩放级别,整型值,范围从1~28。默认为最大
        infoUrl, // 在查看位置界面底部显示的超链接,可点击跳转
      });
    },
  },
};
</script>
